
def get_k_meres(k)
	"ATGC".split("").repeated_permutation(k).to_a.map(&:join).sort
end

def count_frequency_array(k_meres, sample)
	frequency_count = Array.new(k_meres.length) { |i| i = 0 }
	while sample.length >= k_meres.first.length
		k_meres.each_with_index do |k_mere, index|
			frequency_count[index] += 1 if sample.start_with?(k_mere)
		end
		sample = sample[1..-1]
	end
	return frequency_count
end


